/*Table structure for table `jw_content` */

DROP TABLE IF EXISTS `jw_content`;

CREATE TABLE `jw_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `tags` varchar(255) DEFAULT NULL,
  `catename` varchar(255) NOT NULL,
  `cateid` int(11) NOT NULL,
  `price` float(9,2) DEFAULT NULL,
  `sellprice` float(9,2) DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL,
  `comefrom` varchar(255) DEFAULT NULL,
  `hits` int(11) NOT NULL DEFAULT '0',
  `defaultpic` varchar(255) NOT NULL,
  `links` varchar(255) DEFAULT NULL,
  `shortcontent` text,
  `content` longtext,
  `isgood` tinyint(4) NOT NULL DEFAULT '0',
  `isfocus` tinyint(2) NOT NULL DEFAULT '0',
  `taxis` int(11) NOT NULL DEFAULT '0',
  `savename` varchar(255) DEFAULT NULL,
  `addtime` datetime DEFAULT NULL,
  `seo_title` varchar(255) DEFAULT NULL,
  `seo_keyword` text,
  `seo_desc` text,
  `qrcode` varchar(255) DEFAULT NULL,
  `sup_pic` varchar(255) DEFAULT NULL,
  `one_pic` varchar(255) DEFAULT NULL,
  `serv_content` text,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=236 DEFAULT CHARSET=utf8;

